	sc.component_id,
	( CASE WHEN sc.issue_count > 0 THEN ''已下发'' ELSE ''未下发'' END ) issue_status,
	(CASE
			WHEN sc.strategy_type LIKE ''%CONTROL_%'' THEN
			 ''管控策略''
			WHEN sc.strategy_type LIKE ''%FIND_%'' THEN
			 ''发现策略''
			WHEN sc.strategy_type LIKE ''%MONITOR_%'' THEN
			 ''监控策略'' ELSE ''''
		END ) strategy_type_name,
		sc.issue_count,
		sc.strategy_id,
		sc.strategy_type,
		component.NAME ,
		component.ip,
		component.port
	FROM
		(
		SELECT
			component_id,
			strategy_type,
			strategy_id,
			( SELECT COUNT ( * ) FROM t_task_strategy_issue_record T WHERE T.strategy_id = raw.strategy_id AND T.component_id = raw.component_id ) AS issue_count
		FROM
			t_task_strategy_issue_record raw
		GROUP BY
			component_id,
			strategy_type,
			strategy_id
		) AS sc
